mapper.insert:
    insert into
        valve_status(
        sw1,sw2
        ,on_sw1,off_sw1
        ,on_sw2,off_sw2
        ,valve_time,cork_time,
        sw1_onLimit,sw2_onLimit
        )
values (
         #{sw1}, #{sw2}
        ,#{on_sw1}, #{off_sw1}
        ,#{on_sw2}, #{off_sw2}
        ,#{valve_time}, #{cork_time}
        ,#{sw1_onLimit},#{sw2_onLimit}
        )
